(function(){
  var fs;
  fs = require('fs-wwatch');
  fs.dowatch_dowatching = {};
  fs.dowatch = function(path, callback){
    if (fs.dowatch_dowatching[path]) {
      return true;
    }
    fs.dowatch_dowatching[path] = true;
    callback();
    return fs.wwatch(path, callback);
  };
  module.exports = fs;
}).call(this);
